package com.zepan.android.widget;import com.zepan.android.widget.AchievePhotoImageView;import android.app.Dialog;import android.content.Context;import android.net.Uri;/** * 选择图片或拍照的对话框，是一个抽象类，用户继承后需实现2个方法提供控件“拍照”和“相册”的资源id * @see AchievePhotoImageView * */public abstract class AchievePhotoDialog extends Dialog {	public AchievePhotoDialog(Context context, int theme) {		super(context, theme);	}	/**	 * 返回“拍照”控件在R中的资源id	 * */	public abstract int getTakePhotoId();	/**	 * 返回“相册”控件在R中的资源id	 * */	public abstract int getAlbumId();	/**	 * 返回拍照后保存的目标uri	 * */	public abstract Uri getTakePhotoDestUri();}